class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();let o=this;$.ajax({type:"post",url:"/comments/create",data:$(o).serialize(),success:function(n){let m=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(m),t.deleteComment($(" .delete-comment-button",m)),new ToggleUpvote($(" .toggle-upvote-button",m)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show(),$(o).trigger("reset")},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n                        <p>\n                            \n                            <small>\n                                <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                            </small>\n                            \n                            ${e.content}\n                            <br>\n                            <small>\n                                ${e.user.name}\n                            </small>\n\n                            <small>\n           \n                                <a class="toggle-upvote-button" data-upvotes = "0" href="/upvotes/toggle/?id=${e._id}&type=Comment">\n                                    0 Upvotes\n                        \n                                </a>   \n                             </small>\n                        </p>    \n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}